import { Component, Input, OnInit } from '@angular/core';
import { Contact } from '../../model/contact';

@Component({
  selector: 'app-contact-form',
  templateUrl: './contact-form.component.html',
  styleUrls: ['./contact-form.component.css']
})
export class ContactFormComponent implements OnInit {

  public GUIDEMPTY:string = '00000000-0000-0000-0000-000000000000';
  @Input() public contact:Contact = new Contact();
  constructor() { }

  ngOnInit() {
  }


  keyPress(event: any) {
    const pattern = /[0-9\+\-\ ]/;

    let inputChar = String.fromCharCode(event.charCode);
    if (event.keyCode != 8 && !pattern.test(inputChar)) {
      event.preventDefault();
    }
  }
}
<h3 class="title mb-2 mt-2">{{contact.contactId == GUIDEMPTY ? "Add":"Update"}} Contact</h3>

<hr/>

  <div class="row mb-2">
    <div class="form-group col-6">
      <label for="fname">First Name</label>
      <input type="text" placeholder="Enter FirstName" [(ngModel)]="contact.firstName" class="form-control" id="fname" required>
    </div>
    <div class="form-group col-6">
      <label for="lname">Last Name</label>
      <input type="text" placeholder="Enter LastName" [(ngModel)]="contact.lastName" class="form-control" id="lname" required>
    </div>
  </div>
  <div class="row mb-2">
    <div class="form-group col-12">
      <label for="companyName">Company Name</label>
      <input type="text" placeholder="Enter company  name" [(ngModel)]="contact.companyName" class="form-control" id="companyName" >
    </div>
  </div>
  <div class="row mb-2">
    <div class="form-group col-12">
      <label for="email">Email</label>
      <input type="text" placeholder="Enter email" [(ngModel)]="contact.email" class="form-control" id="email" >
    </div>
  </div>
  <div class="row mb-2">
    <div class="form-group col-12">
      <label for="cnumber">Contact Number</label>
      <input type="text" placeholder="Enter contact number" [(ngModel)]="contact.contactNumber" (keypress)="keyPress($event)" minlength=10 maxlength=13 class="form-control" id="cnumber">
    </div>
  </div>
